import React, { useState } from "react";
import userBusiness from "./services/userBusiness";
import ReactDOM from "react-dom";

import "./styles.css";

function App() {

       const [isSubmitted, setIsSubmitted] = useState(false);
    // const [name, setName] = useState("");
    // const [lastName, setLastName] = useState("");
    // const [companyName, setCompanyName] = useState("");
    // const [email, setEmail] = useState("");
    // const [password, setPassword] = useState("");
    const [data, setData] = useState({
        name:"",
        lastName:"",
        companyName:"",
        email:"",
        password:""
    });




    const handleSubmit = () => {
        //Prevent page reload
        const handleChange = e => {
            const { name, value } = e.target;
            setData(prevState => ({
                ...prevState,
                [name]: value
            }));
        };



        //let { name, lastName, companyName,email,password } = document.forms[0];


        //const register = () => userBusiness.save(user);
        console.log(data.name);

        //updateMyArray( arr => [...arr, `${arr.length}`]
    };


    // JSX code for login form
    const renderForm = (
        <div className="form">
            <form onSubmit={handleSubmit}>
                <div className="input-container">
                    <label>Name </label>
                    {/*<input type="text" value={data.name}  onChange={(e) => (data=>data.name).setData(e.target.value)} required />*/}
                    <input type="text" value={data.name}  onChange={handleSubmit} name="name"  required />
                </div>
                <div className="input-container">
                    <label>Last Name </label>
                    <input type="text" value={data.lastName}  onChange={handleSubmit} name="lastName" required />
                </div>
                <div className="input-container">
                    <label>Company Name </label>
                    <input type="text" value={data.companyName}  onChange={handleSubmit} name="companyName" required />
                </div>
                <div className="input-container">
                    <label>Email</label>
                    <input type="email" value={data.email}  onChange={handleSubmit}name="email" required />
                </div>
                <div className="input-container">
                    <label>Password </label>
                    <input type="password" value={data.password}  onChange={handleSubmit}name="password" required />
                </div>
                <div className="button-container">
                    <input type="submit" />
                </div>
            </form>
        </div>
    );

    return (
        <div className="app">
            <div className="login-form">
                <div className="title">Sign Up</div>
                {isSubmitted ? <div>User is successfully logged in</div> : renderForm}
            </div>
        </div>
    );
}

export default App;
